mapper.insert:
    insert into apps_user_activity(phone_no, apps_type, activity, created_on)
	select phone_no, 'android', trx_code, created_on
	from ${tableName}

mapper.select:
select ua.phone_no as phoneNo, ua.apps_type as appsType, 
	ua.activity as activity, ua.created_on as createdOn,
	@row_number:=@row_number+1 AS rowNum
from apps_user_activity ua
	,(SELECT @row_number:=0) AS t


		AND ua.created_on >= #{startDate}


		AND ua.created_on <= #{endDate}
