mapper.select:
select jumlah 
from (
	SELECT count(1) as jumlah , DATE_FORMAT(created_on, '%d/%m/%Y %H:%i') as tanggal
	FROM ${tableName} 
	where MINUTE(created_on) = MINUTE(#{createdOn}) and HOUR(created_on) =  HOUR(#{createdOn})
	group by YEAR(created_on), MONTH(created_on), DATE(created_on), HOUR(created_on), MINUTE(created_on)
) x 
order by jumlah asc
